mapper.select:
SELECT no as cartId, user_id as userId, product_id as productId, price as productPrice, file_path as productFilePath, gname as productName, amount, price*amount as totalPrice
FROM member m, progoods p, cart c
WHERE m.id = c.user_id and p.gno = c.product_id;

mapper.update:
UPDATE cart
SET amount = #{amount}
WHERE cart_id = #{cartId}

mapper.select:
SELECT sum(price*amount)
FROM member m, progoods p, cart c
WHERE m.id = c.user_id and p.gno = c.product_id;
